			alter table Accounts_Temp modify (RecordStatus varchar2(50), RoleCode varchar2(100), NextRoleCode varchar2(200), TaskId varchar2(50), NextTaskId varchar2(200), RecordType varchar2(50));

			update Accounts_Temp m 
			  set (m.RecordStatus, m.RoleCode, m.NextRoleCode, m.TaskId, m.NextTaskId, m.RecordType) = (select t.RecordStatus, t.RoleCode, t.NextRoleCode, t.TaskId, t.NextTaskId, t.RecordType from sai_temp t where t.AccountID = m.AccountID)
			  where exists (select 1 from sai_temp t where t.AccountID = m.AccountID);

			drop table sai_temp;


