   create table sai_temp as select * from AgreementFieldDetails;

   update AgreementFieldDetails set RecordStatus = null, RoleCode = null, NextRoleCode = null, TaskId = null, NextTaskId = null, RecordType = null;

   alter table AgreementFieldDetails modify (RecordStatus varchar2(50), RoleCode varchar2(100), NextRoleCode varchar2(200), TaskId varchar2(50), NextTaskId varchar2(200), RecordType varchar2(50));

   update AgreementFieldDetails m 
     set (m.RecordStatus, m.RoleCode, m.NextRoleCode, m.TaskId, m.NextTaskId, m.RecordType) = (select t.RecordStatus, t.RoleCode, t.NextRoleCode, t.TaskId, t.NextTaskId, t.RecordType from sai_temp t where t.FinReference = m.FinReference)
     where exists (select 1 from sai_temp t where t.FinReference = m.FinReference);

   drop table sai_temp;


