    create table sai_temp as select * from BMTSegments;
 
    update BMTSegments set RecordStatus = null, RoleCode = null, NextRoleCode = null, TaskId = null, NextTaskId = null, RecordType = null;
 
    alter table BMTSegments modify (RecordStatus varchar2(50), RoleCode varchar2(100), NextRoleCode varchar2(200), TaskId varchar2(50), NextTaskId varchar2(200), RecordType varchar2(50));
 
    update BMTSegments m 
      set (m.RecordStatus, m.RoleCode, m.NextRoleCode, m.TaskId, m.NextTaskId, m.RecordType) = (select t.RecordStatus, t.RoleCode, t.NextRoleCode, t.TaskId, t.NextTaskId, t.RecordType from sai_temp t where t.SegmentCode = m.SegmentCode)
      where exists (select 1 from sai_temp t where t.SegmentCode = m.SegmentCode);
 
