alter table LMTSharesDetail drop constraint DF_LMTSharesDetail_RecordType;
 
    alter table LMTSharesDetail alter column RecordStatus varchar(50);
    alter table LMTSharesDetail alter column RoleCode varchar(100);
    alter table LMTSharesDetail alter column NextRoleCode varchar(200);
    alter table LMTSharesDetail alter column TaskId varchar(50);
    alter table LMTSharesDetail alter column NextTaskId varchar(200);
    alter table LMTSharesDetail alter column RecordType varchar(50);

    
    alter table LMTSharesDetail modify RecordStatus default null;
alter table LMTSharesDetail modify RoleCode default null;
alter table LMTSharesDetail modify NextRoleCode default null;
alter table LMTSharesDetail modify TaskId default null;
alter table LMTSharesDetail modify NextTaskId default null;
alter table LMTSharesDetail modify RecordType default null;
 
    create table sai_temp as select * from LMTSharesDetail;
 
    update LMTSharesDetail set RecordStatus = null, RoleCode = null, NextRoleCode = null, TaskId = null, NextTaskId = null, RecordType = null;
 
    alter table LMTSharesDetail modify (RecordStatus varchar2(50), RoleCode varchar2(100), NextRoleCode varchar2(200), TaskId varchar2(50), NextTaskId varchar2(200), RecordType varchar2(50));
 
    update LMTSharesDetail m 
      set (m.RecordStatus, m.RoleCode, m.NextRoleCode, m.TaskId, m.NextTaskId, m.RecordType) = (select t.RecordStatus, t.RoleCode, t.NextRoleCode, t.TaskId, t.NextTaskId, t.RecordType from sai_temp t where t.LoanRefNumber = m.LoanRefNumber and t.CompanyName = m.CompanyName)
      where exists (select 1 from sai_temp t where t.LoanRefNumber = m.LoanRefNumber and t.CompanyName = m.CompanyName);
 
    drop table sai_temp;

    
