alter table RejectFinDefermentDetail alter column RoleCode varchar(100);
alter table RejectFinDefermentDetail alter column NextRoleCode varchar(200);
alter table RejectFinDefermentDetail alter column TaskId varchar(50);
alter table RejectFinDefermentDetail alter column NextTaskId varchar(200);
alter table RejectFinDefermentDetail alter column RecordType varchar(50);

   
   create table sai_temp as select * from RejectFinDefermentDetail;

   update RejectFinDefermentDetail set RecordStatus = null, RoleCode = null, NextRoleCode = null, TaskId = null, NextTaskId = null, RecordType = null;

   alter table RejectFinDefermentDetail modify (RecordStatus varchar2(50), RoleCode varchar2(100), NextRoleCode varchar2(200), TaskId varchar2(50), NextTaskId varchar2(200), RecordType varchar2(50));

   update RejectFinDefermentDetail m 
     set (m.RecordStatus, m.RoleCode, m.NextRoleCode, m.TaskId, m.NextTaskId, m.RecordType) = (select t.RecordStatus, t.RoleCode, t.NextRoleCode, t.TaskId, t.NextTaskId, t.RecordType from sai_temp t where t.FinReference = m.FinReference and t.DeferedSchdDate = m.DeferedSchdDate and t.DeferedRpyDate = m.DeferedRpyDate)
     where exists (select 1 from sai_temp t where t.FinReference = m.FinReference and t.DeferedSchdDate = m.DeferedSchdDate and t.DeferedRpyDate = m.DeferedRpyDate);

   drop table sai_temp;


