			update Accounts set acActive = null, acBlocked = null, acClosed = null, custSysAc = null, internalAc = null;
 
			alter table Accounts modify acActive number(1);
			alter table Accounts modify acBlocked number(1);
			alter table Accounts modify acClosed number(1);
			alter table Accounts modify custSysAc number(1);
			alter table Accounts modify internalAc number(1);
 
			update Accounts m set (m.acActive, m.acBlocked, m.acClosed, m.custSysAc, m.internalAc) = (select t.acActive, t.acBlocked, t.acClosed, t.custSysAc, t.internalAc from sai_temp t where t.AccountId = m.AccountId) where exists (select 1 from sai_temp t where t.AccountId = m.AccountId);
 
			alter table Accounts modify acActive number(1) not null;
