			update RMTFinanceTypes m set (m.allowDownpayPgm, m.allowRIAInvestment, m.alwAdvanceRent, m.alwBPI, m.alwCreditIns, m.alwManualSteps, m.alwMultiPartyDisb, m.alwPlanDeferment, m.applyGrcPricing, m.applyODPenalty, m.applyRpyPricing, m.droplineOD, m.equalRepayment, m.finAlwRateChangeAnyDate, m.finCollateralOvrride, m.finCollateralReq, m.finCommitmentOvrride, m.finCommitmentReq, m.finDepreciationReq, m.finGrcIsIntCpz, m.finGrcIsRvwAlw, m.finIsActive, m.finIsAlwDifferment, m.finIsAlwEarlyRpy, m.finIsAlwEarlySettle, m.fInIsAlwGrace, m.finIsAlwGrcRepay, m.finIsAlwMD, m.finIsAlwPartialRpy, m.finIsDwPayRequired, m.finIsGenRef, m.finIsIntCpz, m.finIsIntCpzAtGrcEnd, m.finIsOpenNewFinAc, m.finIsOpenPftPayAcc, m.finIsRvwAlw, m.finOrgPrfUnchanged, m.finPftUnChanged, m.finRepayPftOnFrq, m.limitRequired, m.manualSchedule, m.oDAllowWaiver, m.oDIncGrcDays, m.overrideLimit, m.planEMICpz, m.planEMIHAlw, m.propertyTakafulReq, m.rateChgAnyDay, m.reAgeCpz, m.rollOverFinance, m.scheduleTakaful, m.stepFinance, m.steppingMandatory, m.takafulMandatory, m.takafulReq, m.tDSApplicable, m.unPlanEMICpz) = (select t.allowDownpayPgm, t.allowRIAInvestment, t.alwAdvanceRent, t.alwBPI, t.alwCreditIns, t.alwManualSteps, t.alwMultiPartyDisb, t.alwPlanDeferment, t.applyGrcPricing, t.applyODPenalty, t.applyRpyPricing, t.droplineOD, t.equalRepayment, t.finAlwRateChangeAnyDate, t.finCollateralOvrride, t.finCollateralReq, t.finCommitmentOvrride, t.finCommitmentReq, t.finDepreciationReq, t.finGrcIsIntCpz, t.finGrcIsRvwAlw, t.finIsActive, t.finIsAlwDifferment, t.finIsAlwEarlyRpy, t.finIsAlwEarlySettle, t.fInIsAlwGrace, t.finIsAlwGrcRepay, t.finIsAlwMD, t.finIsAlwPartialRpy, t.finIsDwPayRequired, t.finIsGenRef, t.finIsIntCpz, t.finIsIntCpzAtGrcEnd, t.finIsOpenNewFinAc, t.finIsOpenPftPayAcc, t.finIsRvwAlw, t.finOrgPrfUnchanged, t.finPftUnChanged, t.finRepayPftOnFrq, t.limitRequired, t.manualSchedule, t.oDAllowWaiver, t.oDIncGrcDays, t.overrideLimit, t.planEMICpz, t.planEMIHAlw, t.propertyTakafulReq, t.rateChgAnyDay, t.reAgeCpz, t.rollOverFinance, t.scheduleTakaful, t.stepFinance, t.steppingMandatory, t.takafulMandatory, t.takafulReq, t.tDSApplicable, t.unPlanEMICpz from sai_temp t where t.FinType = m.FinType) where exists (select 1 from sai_temp t where t.FinType = m.FinType);
 
