drop view promotioninsurances_view; drop view promotioninsurances_aview; drop view promotioninsurances_tview ;
update PromotionInsurances set CalType = 0 where CalType is null; alter table PromotionInsurances alter column CalType DROP DEFAULT; alter table PromotionInsurances alter column CalType type smallint using CalType::smallint ;
