35.GenDeptDesc SalesDepartmentDesc , T1.QuickDisb , T1.WifReference, T1.UnPlanEMIHLockPeriod , T1.UnPlanEMICpz, T1.ReAgeCpz, T1.MaxUnplannedEmi, T1.MaxReAgeHolidays, T1.AvailedUnPlanEmi, T1.AvailedReAgeH FROM FinanceMain T1 INNER JOIN RMTFinanceTypes T7 ON T1.FinType = T7.FinType LEFT OUTER JOIN RMTBranches T12 ON T1.FinBranch = T12.BranchCode LEFT OUTER JOIN FINPFTDETAILS T17 ON T17.FINREFERENCE = T1.FINREFERENCE LEFT OUTER JOIN StepPolicyHeader T24 ON T1.StepPolicy = T24.PolicyCode LEFT OUTER JOIN BMTCustStatusCodes T27 ON T1.finStatus = T27.CustStsCode LEFT OUTER JOIN RMTGenDepartments T28 ON T1.AccountsOfficer = T28.GenDepartment LEFT OUTER JOIN RelationshipOfficers T30 ON T1.DSACode = T30.ROfficerCode LEFT OUTER JOIN RelationshipOfficers T33 ON T1.ReferralId = T33.ROfficerCode LEFT OUTER JOIN RelationshipOfficers T34 ON T1.DmaCode = T34.ROfficerCode LEFT OUTER JOIN RMTGenDepartments T35 ON T1.SalesDepartment = T35.GenDepartment INNER JOIN BMTProduct T32 ON T7.FinCategory = T32.ProductCode WHERE (NOT EXISTS (SELECT 1 Expr1 FROM FinanceMain_Temp WHERE (FINREFERENCE = T1.FINREFERENCE)));
Create or replace view PayOrderIssueHeader_AView AS SELECT T1.FinReference,T4.CustCIF,T4.PhoneNumber,T4.CustShrtName,T3.FinType,T3.FinTypeDesc,T3.FinCategory,T4.CustCRCPR, T1.TotalPOAmount,T1.TotalPOCount, T1.IssuedPOAmount,T1.IssuedPOCount,T1.PODueAmount,T1.PODueCount,T2.FinCcy , T2.NumberOfTerms, T2.GraceTerms , T2.FinStartDate , T2.MaturityDate, T2.FinAmount, T2.FeeChargeAmt, T2.DownPaySupl, T2.TotalProfit, T2.EffectiveRateOfReturn , T2.ProfitDaysBasis,T2.CustID, T1.Version,T1.LastMntBy,T1.LastMntOn,T1.RecordStatus,T1.RoleCode, T1.NextRoleCode,T1.TaskId,T1.NextTaskId,T1.RecordType,T1.WorkflowId,T3.AlwMultiPartyDisb,T2.quickDisb,T2.LoanApproved FROM PayOrderIssueHeader T1 INNER JOIN ( SELECT FinCcy,FinType,CustID,NumberOfTerms,GraceTerms,FinStartDate,FinReference,1 LoanApproved, MaturityDate,FinAmount,FeeChargeAmt,DownPaySupl,TotalProfit,EffectiveRateOfReturn,ProfitDaysBasis,quickDisb FROM FinanceMain UNION ALL SELECT FinCcy,FinType,CustID,NumberOfTerms,GraceTerms,FinStartDate,FinReference,1 LoanApproved, MaturityDate,FinAmount,FeeChargeAmt,DownPaySupl,TotalProfit,EffectiveRateOfReturn,ProfitDaysBasis,quickDisb FROM FinanceMain_Temp WHERE NOT EXISTS( SELECT 1 FROM FinanceMain WHERE FinReference = FinanceMain_Temp.finreference ) ) T2 ON T1.FinReference = T2.FinReference INNER JOIN RMTFinanceTypes T3 ON T2.FinType=T3.FinType INNER JOIN Customers T4 ON T2.CustID = T4.CustID;
