begin
execute 'CREATE SEQUENCE
SeqBuilderCompany START 1 INCREMENT BY 1 ';
end;
$$;

CREATE TABLE SeqBuilderCompany(
SeqNo bigint
IDENTITY(1,1) NOT NULL,
Value int NOT NULL
);
