  CREATE FUNCTION [dbo].[FN_Discrepancy] (@Discrepancy nvarchar(200))
		RETURNS  nvarchar(max)
		AS
		BEGIN
		 DECLARE @CNTOfWarnings int
		 DECLARE @CNTOfNosInErrorMsg int
		 DECLARE @CNT int
		 DECLARE @ErrorSeverity as Varchar(10)
		 DECLARE @DiscrepancyList as nvarchar(max)
		 DECLARE @Discrepancytemp as nvarchar(max)
		 DECLARE @Discrepancytemp1 as nvarchar(max)
		 DECLARE @DiscrepancyErrorMsg as nvarchar(max)
		 DECLARE @Discrepancy0 as nvarchar(50)
		 
		 
			SET @Discrepancy=@Discrepancy+';'
			SET @DiscrepancyList=''
			
			set @CNTOfWarnings= LEN(@Discrepancy)-LEN(REPLACE(@Discrepancy,';',''))
			WHILE (@CNTOfWarnings >0) 
			BEGIN
			SET @Discrepancytemp1 = Substring(@Discrepancy,1,CHARINDEX(';',@Discrepancy)-1)
			IF (LEN(@Discrepancytemp1)=5) BEGIN SET @Discrepancytemp =@Discrepancytemp1 END
			ELSE BEGIN SET @Discrepancytemp = STUFF(@Discrepancytemp1,LEN(@Discrepancytemp1),LEN(@Discrepancytemp1),'|1') END
			Select @DiscrepancyErrorMsg=ErrorMessage From ErrorDetails Where ErrorCode = Substring(@Discrepancytemp,1,5)
			Select @ErrorSeverity=ErrorSeverity From ErrorDetails Where ErrorCode = Substring(@Discrepancytemp,1,5)
				IF  @ErrorSeverity='W' and @DiscrepancyList not like '%Warning%'
				BEGIN SET @DiscrepancyList=@DiscrepancyList+' <br> <font color="#963805"><b>Warning</b></font>'	END
				IF  @ErrorSeverity='E' and @DiscrepancyList not like '%Error%'
				BEGIN SET @DiscrepancyList=@DiscrepancyList+' <br> <font color="red"><b>Error</b></font>'	END				
			SET @Discrepancytemp =REPLACE(@Discrepancytemp,Substring(@Discrepancytemp,1,6),'')
			set @CNTOfNosInErrorMsg= LEN(@Discrepancytemp)-LEN(REPLACE(@Discrepancytemp,'|',''))
			SET @CNT=0
				WHILE (@CNTOfNosInErrorMsg >0) 	
				BEGIN
				SET @Discrepancy0=Substring(@Discrepancytemp,1,CHARINDEX('|',@Discrepancytemp)-1)
				SET @DiscrepancyErrorMsg = REPLACE(@DiscrepancyErrorMsg,'{'+Convert(varchar(20),@CNT)+'}',@Discrepancy0)
				SET @CNT = @CNT+1
				SET @CNTOfNosInErrorMsg = @CNTOfNosInErrorMsg-1
				SET @Discrepancytemp = REPLACE(@Discrepancytemp,@Discrepancy0+'|','')
				END
			
						
			SET	@Discrepancy = REPLACE(@Discrepancy,@Discrepancytemp1+';','')
			
			
			SET @DiscrepancyList=@DiscrepancyList+' <br>  <font size="3"><b>. </b></font>'+@DiscrepancyErrorMsg
			SET @CNTOfWarnings =@CNTOfWarnings-1
			
			END			
			RETURN Substring(@DiscrepancyList,7,LEN(@DiscrepancyList))
			
		END

GO


Create view RPT_DiscrepancyInfo_View
AS
Select [dbo].[FN_Discrepancy] (F.Discrepancy) Discrepancy,F.FinReference,C.CustCIF CustID,
		C.CustFName,F.FinCcy,F.FinBranch,F.MaturityDate,F.FinStartDate,F.FinType,
		ISNULL (S.UsrFName,'')+' '+ISNULL(S.UsrMName,' ')+' '+ISNULL(S.UsrLName,' ') UserName,
		F.FinAmount,B.BranchDesc,R.CcyEditField
From FinanceMain F INNER JOIN
	 Customers C ON C.CustID=F.CustID INNER JOIN
	 RMTCurrencies R ON R.CcyCode=F.FinCcy INNER JOIN
	 RMTBranches B ON B.BranchCode=F.FinBranch LEFT JOIN
	 PFFLIVAudit..AdtFinanceMain T1 ON T1.FinReference=F.FinReference and T1.RoleCode='CREDIT_ADMIN_MANAGER'
	 LEFT JOIN	SecUsers S On S.UsrID=T1.LastMntBy
Where ISNULL(F.Discrepancy,'')!='' and F.Discrepancy like 'DS%'
GO



