changeSet.sql:
    
    alter table AdtAccounts drop constraint DF_AdtAccounts_RecordStatus;
alter table AdtAccounts drop constraint DF_AdtAccounts_RoleCode;
alter table AdtAccounts drop constraint DF_AdtAccounts_NextRoleCode;
alter table AdtAccounts drop constraint DF_AdtAccounts_TaskId;
alter table AdtAccounts drop constraint DF_AdtAccounts_NextTaskId;
alter table AdtAccounts drop constraint DF_AdtAccounts_RecordType;
 
    alter table AdtAccounts alter column RecordStatus varchar(50);
    alter table AdtAccounts alter column RoleCode varchar(100);
    alter table AdtAccounts alter column NextRoleCode varchar(200);
    alter table AdtAccounts alter column TaskId varchar(50);
    alter table AdtAccounts alter column NextTaskId varchar(200);
    alter table AdtAccounts alter column RecordType varchar(50);

    
    alter table AdtAccounts modify RecordStatus default null;
alter table AdtAccounts modify RoleCode default null;
alter table AdtAccounts modify NextRoleCode default null;
alter table AdtAccounts modify TaskId default null;
alter table AdtAccounts modify NextTaskId default null;
alter table AdtAccounts modify RecordType default null;
 
    create table sai_temp as select * from AdtAccounts;
 
    update AdtAccounts set RecordStatus = null, RoleCode = null, NextRoleCode = null, TaskId = null, NextTaskId = null, RecordType = null;
 
    alter table AdtAccounts modify (RecordStatus varchar2(50), RoleCode varchar2(100), NextRoleCode varchar2(200), TaskId varchar2(50), NextTaskId varchar2(200), RecordType varchar2(50));
 
    update AdtAccounts m 
      set (m.RecordStatus, m.RoleCode, m.NextRoleCode, m.TaskId, m.NextTaskId, m.RecordType) = (select t.RecordStatus, t.RoleCode, t.NextRoleCode, t.TaskId, t.NextTaskId, t.RecordType from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage)
      where exists (select 1 from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage);
 
    drop table sai_temp;

    
