    alter table AdtSystemInternalAccountDef alter column RoleCode varchar(100);
    alter table AdtSystemInternalAccountDef alter column NextRoleCode varchar(200);
    alter table AdtSystemInternalAccountDef alter column TaskId varchar(50);
    alter table AdtSystemInternalAccountDef alter column NextTaskId varchar(200);
    alter table AdtSystemInternalAccountDef alter column RecordType varchar(50);

    
    alter table AdtSystemInternalAccountDef modify RecordStatus default null;
alter table AdtSystemInternalAccountDef modify RoleCode default null;
alter table AdtSystemInternalAccountDef modify NextRoleCode default null;
alter table AdtSystemInternalAccountDef modify TaskId default null;
alter table AdtSystemInternalAccountDef modify NextTaskId default null;
alter table AdtSystemInternalAccountDef modify RecordType default null;
 
    create table sai_temp as select * from AdtSystemInternalAccountDef;
 
    update AdtSystemInternalAccountDef set RecordStatus = null, RoleCode = null, NextRoleCode = null, TaskId = null, NextTaskId = null, RecordType = null;
 
    alter table AdtSystemInternalAccountDef modify (RecordStatus varchar2(50), RoleCode varchar2(100), NextRoleCode varchar2(200), TaskId varchar2(50), NextTaskId varchar2(200), RecordType varchar2(50));
 
    update AdtSystemInternalAccountDef m 
      set (m.RecordStatus, m.RoleCode, m.NextRoleCode, m.TaskId, m.NextTaskId, m.RecordType) = (select t.RecordStatus, t.RoleCode, t.NextRoleCode, t.TaskId, t.NextTaskId, t.RecordType from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage)
      where exists (select 1 from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage);
 
    drop table sai_temp;


