			update AdtCustomerDocuments set custDocIsAcrive = 0 where custDocIsAcrive is null;
			update AdtCustomerDocuments set custDocIsVerified = 0 where custDocIsVerified is null;
 
			create table sai_temp as select * from AdtCustomerDocuments;
 
			alter table AdtCustomerDocuments modify (custDocIsAcrive NULL);
 
			update AdtCustomerDocuments set custDocIsAcrive = null, custDocIsVerified = null;
 
			alter table AdtCustomerDocuments modify custDocIsAcrive number(1);
			alter table AdtCustomerDocuments modify custDocIsVerified number(1);
 
			update AdtCustomerDocuments m set (m.custDocIsAcrive, m.custDocIsVerified) = (select t.custDocIsAcrive, t.custDocIsVerified from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage) where exists (select 1 from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage);
 
			alter table AdtCustomerDocuments modify custDocIsAcrive number(1) not null;
			alter table AdtCustomerDocuments modify custDocIsVerified number(1) not null;
 
			drop table sai_temp;

		
			alter table AdtCustomerEmpDetails drop constraint DF_AdtCustEmpDs_CurrEmployer;
 
			update AdtCustomerEmpDetails set currentEmployer = 0 where currentEmployer is null;
			alter table AdtCustomerEmpDetails alter column currentEmployer bit not null;

		
			alter table AdtCustomerEmpDetails modify currentEmployer default null;
 
			update AdtCustomerEmpDetails set currentEmployer = 0 where currentEmployer is null;
 
			create table sai_temp as select * from AdtCustomerEmpDetails;
 
			alter table AdtCustomerEmpDetails modify (currentEmployer NULL);
 
			update AdtCustomerEmpDetails set currentEmployer = null;
 
			alter table AdtCustomerEmpDetails modify currentEmployer number(1);
 
			update AdtCustomerEmpDetails m set (m.currentEmployer) = (select t.currentEmployer from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage) where exists (select 1 from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage);
 
			alter table AdtCustomerEmpDetails modify currentEmployer number(1) not null;
 
			drop table sai_temp;

		
			update AdtCustomerGroups set custGrpIsActive = 0 where custGrpIsActive is null;
 
			create table sai_temp as select * from AdtCustomerGroups;
 
			alter table AdtCustomerGroups modify (custGrpIsActive NULL);
 
			update AdtCustomerGroups set custGrpIsActive = null;
 
			alter table AdtCustomerGroups modify custGrpIsActive number(1);
 
			update AdtCustomerGroups m set (m.custGrpIsActive) = (select t.custGrpIsActive from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage) where exists (select 1 from sai_temp t where t.AuditId = m.AuditId and t.AuditDate = m.AuditDate and t.AuditSeq = m.AuditSeq and t.AuditImage = m.AuditImage);
 
			alter table AdtCustomerGroups modify custGrpIsActive number(1) not null;
 
			drop table sai_temp;


