			 declare ex number;
  		 begin
  		 Select MAX(SeqNo)+1 into ex  from (Select SeqNo from SeqProspectCustomer_bk )T;
      	 execute immediate 'CREATE SEQUENCE SeqProspectCustomer START WITH ' || ex || ' INCREMENT BY 1 MAXVALUE 9223372036854775807 NOCYCLE';  
  		 end;

			
			Select * into SeqProspectCustomer_bk  from SeqProspectCustomer;
			DROP table SeqProspectCustomer;
   			
			Declare @ex bigint;
