			CREATE TABLE SeqProspectCustomer(
			SeqNo 				bigint IDENTITY(1,1) NOT NULL,
			Value 				int NOT NULL
			);	
		
			Exec('DBCC CHECKIDENT(''SeqProspectCustomer'', RESEED,'+@ex+' )');
			
