DROP INDEX SecOperationRoles.IX_OR_OID_RID;
DROP INDEX SecRoles.IX_R_RA;

DROP INDEX IX_UO_UID_OID;
DROP INDEX IX_OR_OID_RID;
DROP INDEX IX_R_RA;

CREATE INDEX IX_UO_UID_OID ON SECUSEROPERATIONS (USRID ASC, OPRID ASC);
CREATE INDEX IX_OR_OID_RID ON SecOperationRoles (OprID ASC, RoleID ASC);
CREATE INDEX IX_R_RA ON SecRoles (RoleApp ASC);
