Update FinanceMain set FinCurrAssetValue = (select SUM(DisbAmount) from FinDisbursementDetails D  WHERE Financemain.FinReference = D.FinReference);
Update FinanceMain set FinCurrAssetValue = FinCurrAssetValue-DownPayment;
Update FinanceMain_Temp set FinCurrAssetValue = (select SUM(DisbAmount) from FinDisbursementDetails D  WHERE FinanceMain_Temp.FinReference = D.FinReference);
Update FinanceMain_Temp set FinCurrAssetValue = FinCurrAssetValue-DownPayment;
Update FinanceMain_Temp set FinCurrAssetValue = 0 WHERE FinCurrAssetValue IS NULL;
Update FinanceMain set FinCurrAssetValue = 0 WHERE FinCurrAssetValue IS NULL;

