	INSERT INTO SECOPERATIONROLES values ((select MAX(OprRoleID)+1 from SecOperationRoles), (select OprID from SecOperations WHERE OprCode='PRESENTMENT_DETAIL_OPERATIONS'), (select RoleID from SecRoles where RoleCd='ROLE_PRESENTMENTDETAILS'),1,1000,CURRENT_TIMESTAMP,'Approved',' ',' ',' ',' ',' ',0);
	
	UPDATE SeqSecRights SET SeqNo = (SELECT MAX(RightID) FROM SecRights);
	UPDATE SeqSecGroups SET SeqNo = (SELECT MAX(GrpID) FROM SecGroups);
	UPDATE SeqSecRoles SET SeqNo = (SELECT MAX(RoleID) FROM SecRoles);
	UPDATE SeqSecOperations SET SeqNo = (SELECT MAX(OprID) FROM SecOperations);
	UPDATE SeqSecGroupRights SET SeqNo = (SELECT MAX(GrpRightID) FROM SecGroupRights);
	UPDATE SeqSecRoleGroups SET SeqNo = (SELECT MAX(RoleGrpID) FROM SecRoleGroups);
	UPDATE SeqSecOperationRoles SET SeqNo = (SELECT MAX(OprRoleID) FROM SecOperationRoles);

 
