	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.ID),0)+1 into v_maxval from
	(select ID from CUSTOMERDOCUMENTS union all select ID from CUSTOMERDOCUMENTS_Temp union all select DocId from DOCUMENTDETAILS union all select DocId from DOCUMENTDETAILS_Temp) T;
	EXECUTE IMMEDIATE 'alter
	sequence SEQDOCUMENTDETAILS restart start with ' || v_maxval;
	end;


	do $$
	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.ID),0)+1 into v_maxval from
	(select ID from CUSTOMERDOCUMENTS union all select ID from CUSTOMERDOCUMENTS_Temp union all select DocId from DOCUMENTDETAILS union all select DocId from DOCUMENTDETAILS_Temp) T;
	EXECUTE 'alter
	sequence SEQDOCUMENTDETAILS restart start with ' || v_maxval;
	end;
	$$;

DECLARE @SQLCmd NVARCHAR(MAX); DECLARE @MAXNO BIGINT; SELECT @MAXNO=COALESCE(max(T.ID),0)+ 1 from (select max(ID) as ID from CUSTOMERDOCUMENTS union all select max(ID) as ID from CUSTOMERDOCUMENTS_Temp union all select max(DocId) as ID from DOCUMENTDETAILS union all select max(DocId) as ID from DOCUMENTDETAILS_Temp) T;
SET @SQLCmd = ' ALTER SEQUENCE SEQDOCUMENTDETAILS RESTART WITH ' + CAST( @MAXNO AS VARCHAR(30)) +' INCREMENT BY 1 MINVALUE -9223372036854775808 MAXVALUE 9223372036854775807 CACHE ';
EXEC SP_EXECUTESQL @SQLCmd;

