SET @SQLCmd = ' ALTER SEQUENCE SEQDOCUMENTDETAILS RESTART WITH ' + CAST( @MAXNO AS VARCHAR(30)) +' INCREMENT BY 1 MINVALUE -9223372036854775808 MAXVALUE 9223372036854775807 CACHE ';
EXEC SP_EXECUTESQL @SQLCmd;

			
