	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.ID),0)+1 into v_maxval from
	(select ID from GSTDETAILS union all select ID from GSTDETAILS_TEMP) T;
	EXECUTE IMMEDIATE 'alter sequence SeqGST_Details
	restart
	start with ' || v_maxval;
	end;


	do $$
	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.ID),0)+1 into v_maxval from
	(select ID from GSTDETAILS union all select ID from GSTDETAILS_TEMP) T;
	EXECUTE 'alter sequence SeqGST_Details restart
	start
	with ' || v_maxval;
	end;
	$$;

