			MERGE INTO FINRECEIPTHEADER RH
            USING (SELECT RECEIPTID, REFERENCE, FM.FINTYPE FROM FINRECEIPTHEADER T1 
			INNER JOIN FINANCEMAIN FM ON FM.FINREFERENCE = T1.REFERENCE
			) RH1 ON (RH1.RECEIPTID = RH.RECEIPTID)
            WHEN MATCHED THEN UPDATE SET 
            FINTYPE = RH1.FINTYPE;


	UPDATE RH SET RH.FINTYPE = FM.FINTYPE
		FROM FINRECEIPTHEADER RH 
		INNER JOIN FINANCEMAIN FM ON FM.FINREFERENCE = RH.REFERENCE;


	UPDATE FINRECEIPTHEADER SET FINTYPE = FM.FINTYPE
		FROM FINANCEMAIN FM 
		WHERE FINRECEIPTHEADER.REFERENCE = FM.FINREFERENCE;

		
			MERGE INTO FINRECEIPTHEADER_TEMP RH
            USING (SELECT RECEIPTID, REFERENCE, FM.FINTYPE FROM FINRECEIPTHEADER_TEMP T1 
			INNER JOIN FINANCEMAIN FM ON FM.FINREFERENCE = T1.REFERENCE
			) RH1 ON (RH1.RECEIPTID = RH.RECEIPTID)
            WHEN MATCHED THEN UPDATE SET 
            FINTYPE = RH1.FINTYPE;


	UPDATE RH SET RH.FINTYPE = FM.FINTYPE
		FROM FINRECEIPTHEADER_TEMP RH 
		INNER JOIN FINANCEMAIN FM ON FM.FINREFERENCE = RH.REFERENCE;


	UPDATE FINRECEIPTHEADER_TEMP SET FINTYPE = FM.FINTYPE
		FROM FINANCEMAIN FM 
		WHERE FINRECEIPTHEADER_TEMP.REFERENCE = FM.FINREFERENCE;

		
			MERGE INTO FINRECEIPTHEADER RH
            USING (SELECT RECEIPTID, REFERENCE, FM.FINTYPE FROM FINRECEIPTHEADER T1 
			INNER JOIN FINANCEMAIN_TEMP FM ON FM.FINREFERENCE = T1.REFERENCE
			) RH1 ON (RH1.RECEIPTID = RH.RECEIPTID)
            WHEN MATCHED THEN UPDATE SET 
            FINTYPE = RH1.FINTYPE;


	UPDATE RH SET RH.FINTYPE = FM.FINTYPE
		FROM FINRECEIPTHEADER RH 
		INNER JOIN FINANCEMAIN_TEMP FM ON FM.FINREFERENCE = RH.REFERENCE;


	UPDATE FINRECEIPTHEADER SET FINTYPE = FM.FINTYPE
		FROM FINANCEMAIN_TEMP FM 
		WHERE FINRECEIPTHEADER.REFERENCE = FM.FINREFERENCE;

		
			MERGE INTO FINRECEIPTHEADER_TEMP RH
            USING (SELECT RECEIPTID, REFERENCE, FM.FINTYPE FROM FINRECEIPTHEADER_TEMP T1 
			INNER JOIN FINANCEMAIN_TEMP FM ON FM.FINREFERENCE = T1.REFERENCE
			) RH1 ON (RH1.RECEIPTID = RH.RECEIPTID)
            WHEN MATCHED THEN UPDATE SET 
            FINTYPE = RH1.FINTYPE;


	UPDATE RH SET RH.FINTYPE = FM.FINTYPE
		FROM FINRECEIPTHEADER_TEMP RH 
		INNER JOIN FINANCEMAIN_TEMP FM ON FM.FINREFERENCE = RH.REFERENCE;


	UPDATE FINRECEIPTHEADER_TEMP SET FINTYPE = FM.FINTYPE
		FROM FINANCEMAIN_TEMP FM 
		WHERE FINRECEIPTHEADER_TEMP.REFERENCE = FM.FINREFERENCE;

	 
