                 update extendedfielddetail set scriptlet='onChange>>   
					  var totalMarketValComp =  zk.Widget.$(jq("$ad_TOTALMARKETVALUE")[0]);
              		  var marketVal =  zk.Widget.$(jq("$ad_MARKETVALUE")[0]).getValue(); 
					  var othChargesVal =  zk.Widget.$(jq("$ad_OTHERCHARGES")[0]).getValue();  
					  if(isNaN(marketVal) || marketVal==="" ){  
		                 	marketVal = 0;  
		                  } 
						 if(isNaN(othChargesVal) || othChargesVal==="" ){  
		                 	othChargesVal = 0;  
		                  } 						  
					    var totMarketVal =  parseInt(marketVal,10) + parseInt(othChargesVal,10) ;
					  totalMarketValComp.setValue(totMarketVal);
					  zAu.send(new zk.Event(totalMarketValComp, "onChange", {"value":totMarketVal.toString()}), 10);' where moduleid=(select moduleid from extendedfieldheader 
				where modulename='COLLATERAL' and submodulename='PROPERTY') and extendedtype=1 and fieldname='OTHERCHARGES';

            
            update extendedfielddetail set scriptlet='onChange>>   
					var marketValComp =  zk.Widget.$(jq("$ad_MARKETVALUE")[0]);
					var totalMarketValComp =  zk.Widget.$(jq("$ad_TOTALMARKETVALUE")[0]);
					var areaVal =  zk.Widget.$(jq("$ad_AREAVALUEDSQFT")[0]).getValue();  
					var ratePSFVal =  zk.Widget.$(jq("$ad_RATEPSF")[0]).getValue();  
					var floorRiseVal =  zk.Widget.$(jq("$ad_FLOORRISE")[0]).getValue().$toNumber();  
					var othChargesVal =  zk.Widget.$(jq("$ad_OTHERCHARGES")[0]).getValue();  
					if(isNaN(areaVal) || areaVal==="" ){  
					} 
					if(isNaN(ratePSFVal) || ratePSFVal==="" ){  
					ratePSFVal = 0;  
					} 
					if(isNaN(floorRiseVal) || floorRiseVal==="" ){  
					floorRiseVal = 0;  
					} 
					if(isNaN(othChargesVal) || othChargesVal==="" ){  
					othChargesVal = 0;  
					} 						  
					var marketVal =  parseInt(ratePSFVal,10) + parseInt(floorRiseVal,10) ;
					var marketVal1 = marketVal * parseInt(areaVal,10);
					var totMarketVal = marketVal1 + parseInt(othChargesVal,10) ;
					marketValComp.setValue(marketVal1);
					totalMarketValComp.setValue(totMarketVal);
					zAu.send(new zk.Event(marketValComp, "onChange", {"value":marketVal1.toString()}), 10);
					zAu.send(new zk.Event(totalMarketValComp, "onChange", {"value":totMarketVal.toString()}), 10);' where moduleid=(select moduleid from extendedfieldheader 
			where modulename='COLLATERAL' and submodulename='PROPERTY') and extendedtype=1 and fieldname='FLOORRISE';


