CREATE FUNCTION GetFinancialYearWithFun()
			RETURNS
			@financialyear TABLE
			(
			-- Add the column definitions for the TABLE variable here
			startDate date,
			enddate date
			)
			AS
			BEGIN
			-- Fill the table variable with the rows for your result set
			Declare @sysmonth int;
			DECLARE @STARTDATE varchar(50);
			DECLARE @ENDDATE varchar(50);
			DECLARE @APPDATE VARCHAR(50);
			select @APPDATE=sysparmvalue from smtparameters where sysparmcode='APP_DATE';
			DECLARE @1 VARCHAR(4);
			set @1='-01';
			DECLARE @3 VARCHAR(4);
			set @3='-03';
			DECLARE @4 VARCHAR(4);
			SET @4='-04';
			DECLARE @31 VARCHAR(4);
			SET @31='-31';
			DECLARE @12 VARCHAR(4);
			SET @12='-12';
			DECLARE @YEAR INT;
			SET @YEAR=YEAR(@APPDATE);
			DECLARE @MONTH INT;
			SET @MONTH=MONTH( @APPDATE);
			select @sysmonth=sysparmvalue from smtparameters where sysparmcode='FINANCIAL_YEAR_START_MONTH';
			if (@sysmonth = 1)
			begin
			set @STARTDATE=CONCAT(@YEAR,@1,@1);
			set @ENDDATE=CONCAT(@YEAR,@12,@31);
			end
			else
			begin
			if (@sysmonth = 4)
			begin
			if(@MONTH<=3)
			begin
			SET @STARTDATE=CONCAT(convert(varchar,@YEAR-1),@4,@1);
			set @ENDDATE=CONCAT(convert(varchar,@YEAR),@3,@31);
			end
			ELSE
			if(@MONTH>3)
			begin
			SET @STARTDATE=CONCAT(convert(varchar,@YEAR),@4,@1);
			set @ENDDATE=CONCAT(convert(varchar,@YEAR+1),@3,@31);
			end
			end
			end
			insert into @financialyear values(@STARTDATE,@ENDDATE);
			RETURN
			END

changeSet.createProcedure:

	CREATE PROCEDURE updateSequence
	-- Add the parameters for the stored procedure here
	@seqName varchar(2000),
	@seqNumber BIGINT
	AS
	BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;
	DECLARE @s nvarchar(max);
	SET @s = N'ALTER SEQUENCE '+ @seqName +' RESTART WITH ' + CAST(@seqNumber AS nvarchar(max));
	
	EXEC (@s);
	
	END

