	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.PromotionId),0)+1 into v_maxval from
	(select PromotionId
	from Promotions union all select PromotionId from Promotions_Temp) T;
	EXECUTE IMMEDIATE 'alter sequence SeqPromotions
	restart
	start with ' || v_maxval;
	end;


	do $$
	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.PromotionId),0)+1 into v_maxval from
	(select
	PromotionId from Promotions union all select PromotionId from Promotions_Temp) T;
	EXECUTE 'alter sequence SeqPromotions restart
	start
	with ' || v_maxval;
	end;
	$$;

			
			DECLARE @SQLCmd NVARCHAR(MAX);
			DECLARE @MAXNO BIGINT;
			SELECT @MAXNO=COALESCE(max(T.PromotionId),0)+1
			from
			(select PromotionId from Promotions union all select PromotionId from Promotions_Temp) T;

			SET @SQLCmd = '
			ALTER
			SEQUENCE [dbo].[SeqPromotions]
			RESTART WITH '
			+ CAST( @MAXNO AS VARCHAR(30)) ;


			EXEC
			SP_EXECUTESQL @SQLCmd;

			
   		     	UPDATE FINRECEIPTHEADER_TEMP SET SUBRECEIPTMODE = null WHERE SUBRECEIPTMODE = '#';
				UPDATE FINRECEIPTHEADER SET SUBRECEIPTMODE = null WHERE SUBRECEIPTMODE = '#';
				UPDATE FINRECEIPTHEADER_TEMP SET RECEIPTMODE = null WHERE RECEIPTMODE = '#';
				UPDATE FINRECEIPTHEADER SET RECEIPTMODE = null WHERE RECEIPTMODE = '#';
