	declare
	v_maxval NUMBER(19);
	begin
	select COALESCE(max(T.RULEID),0)+1 into v_maxval from
	(select
	RULEID from RULES union all select RULEID from RULES_Temp) T;
	EXECUTE IMMEDIATE
	'alter sequence SeqRules restart start with ' || v_maxval;
	end;


	do $$
	declare
	v_maxval BIGINT;
	begin
	select COALESCE(max(T.RULEID),0)+1 into v_maxval from
	(select RULEID from RULES union all select RULEID from RULES_Temp) T;
	EXECUTE 'alter
	sequence SeqRules restart start with ' || v_maxval;
	end;
	$$;

		
