				UPDATE SECGROUPS SET GRPCODE = 'PMAY_MAKER' WHERE GRPCODE = 'PMAY_Maker';
				UPDATE SECGROUPS SET GRPCODE = 'PMAY_APPROVER' WHERE GRPCODE = 'PMAY_Approver';
				
				DELETE FROM SECROLEGROUPS WHERE GRPID IN (SELECT GRPID FROM SECGROUPS WHERE GRPCODE IN('PMAY_MAKER', 'PMAY_APPROVER')) AND ROLEID IN (SELECT ROLEID FROM SECROLES WHERE ROLECD IN('MSTGRP1_MAKER', 'MSTGRP1_APPROVER'));
				DELETE FROM SECROLEGROUPS WHERE GRPID IN (SELECT GRPID FROM SECGROUPS WHERE GRPCODE IN('PMAY_MAKER', 'PMAY_APPROVER')) AND ROLEID IN (SELECT ROLEID FROM SECROLES WHERE ROLECD IN('PMAY_MAKER', 'PMAY_APPROVER'));
				DELETE FROM SECOPERATIONROLES WHERE ROLEID IN (SELECT ROLEID FROM SECROLES WHERE ROLECD IN('PMAY_MAKER', 'PMAY_APPROVER'));
				DELETE FROM SECROLES WHERE ROLECD = 'PMAY_MAKER';
				DELETE FROM SECROLES WHERE ROLECD = 'PMAY_APPROVER';
				
