	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.DEVIATIONID),0)+1 into v_maxval from
	(select DEVIATIONID from FinanceDeviations union all select DEVIATIONID from FinanceDeviations_temp) T;
	EXECUTE IMMEDIATE 'alter sequence SeqDeviations
	restart
	start with ' || v_maxval;
	end;


	do $$
	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.DEVIATIONID),0)+1 into v_maxval from
	(select DEVIATIONID from FinanceDeviations union all select DEVIATIONID from FinanceDeviations_temp) T;
	EXECUTE 'alter sequence SeqDeviations restart
	start
	with ' || v_maxval;
	end;
	$$;

DECLARE @SQLCmd NVARCHAR(MAX); DECLARE @MAXNO BIGINT; SELECT @MAXNO=COALESCE(max(T.DEVIATIONID),0)+1 from (select DEVIATIONID from FinanceDeviations union all select DEVIATIONID from FinanceDeviations_temp) T;
SET @SQLCmd = ' CREATE SEQUENCE [dbo].[SeqDeviations] AS [bigint] START WITH ' + CAST( @MAXNO AS VARCHAR(30)) + 'INCREMENT BY 1 MINVALUE -9223372036854775808 MAXVALUE 9223372036854775807 CACHE ';
EXEC SP_EXECUTESQL @SQLCmd;

		
