	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.NoteId),0)+1 into v_maxval from
	(select NoteId from Notes) T;
	EXECUTE IMMEDIATE 'alter
	sequence SEQNotes restart start with ' || v_maxval;
	end;


	do $$
	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.NoteId),0)+1 into v_maxval from
	(select NoteId from Notes) T;
	EXECUTE 'alter
	sequence SEQNotes restart start with ' || v_maxval;
	end;
	$$;

			
			DECLARE @SQLCmd NVARCHAR(MAX);
			DECLARE @MAXNO BIGINT;
			SELECT
			@MAXNO=COALESCE(max(T.NoteId),0)+1 from
			(select NoteId from Notes) T;

			SET @SQLCmd = '
			CREATE
			SEQUENCE [dbo].[SEQNotes]
			AS [bigint]
			START WITH '
			+ CAST( @MAXNO
			AS VARCHAR(30)) +
			'INCREMENT BY 1
			MINVALUE
			-9223372036854775808
			MAXVALUE 9223372036854775807
			CACHE ';


			EXEC
			SP_EXECUTESQL
			@SQLCmd;


	do $$
	declare
	v_maxval BIGINT;
	begin
	SELECT COALESCE(MAX(T.FEETYPEID),0)+1 INTO V_MAXVAL FROM
	(SELECT FEETYPEID FROM FEETYPES UNION ALL SELECT FEETYPEID FROM FEETYPES_TEMP) T;
	EXECUTE 'alter sequence SEQFEETYPES
	restart start with ' || v_maxval;
	end;
	$$;


