				alter table ogl_transactions_file add column ConvertedCreditamountT numeric(18,0);
				alter table ogl_transactions_file add column ConvertedDebitamountT numeric(18,0);
				update ogl_transactions_file set ConvertedDebitamount = 0 where converteddebitamount='';
                update ogl_transactions_file set ConvertedCreditAmount = 0 where ConvertedCreditAmount='';
				update ogl_transactions_file set ConvertedDebitamountT = cast(ConvertedDebitamount as numeric(18,0)), ConvertedCreditamountT = cast(ConvertedCreditamount as numeric(18,0));
				update ogl_transactions_file set ConvertedDebitamount = 0, ConvertedCreditamount = 0;
				alter table ogl_transactions_file alter column ConvertedCreditamount type numeric(18,0) using ConvertedCreditamount::numeric(18,0); 
				alter table ogl_transactions_file alter column ConvertedDebitamount type numeric(18,0) using ConvertedDebitamount::numeric(18,0);
				update ogl_transactions_file set ConvertedDebitamount = ConvertedDebitamountT, ConvertedCreditamount = ConvertedCreditamountT;
				alter table ogl_transactions_file drop column ConvertedCreditamountT;
				alter table ogl_transactions_file drop column ConvertedDebitamountT;

			
				alter table ogl_transactions_log add column ConvertedCreditAmountT numeric(18,0);
				alter table ogl_transactions_log add column ConvertedDebitAmountT numeric(18,0);
				update ogl_transactions_log set ConvertedDebitamount = 0 where converteddebitamount='';
                update ogl_transactions_log set ConvertedCreditAmount = 0 where ConvertedCreditAmount='';
				update ogl_transactions_log set ConvertedDebitamountT = Cast(ConvertedDebitamount as numeric(18,0)), ConvertedCreditamountT = Cast(ConvertedCreditamount as numeric(18,0));
				update ogl_transactions_log set ConvertedDebitamount = 0, ConvertedCreditAmount = 0;
				alter table ogl_transactions_log alter column ConvertedCreditAmount type numeric(18,0) USING ConvertedCreditAmount::numeric(18,0); 
				alter table ogl_transactions_log alter column ConvertedDebitAmount type numeric(18,0) USING ConvertedDebitAmount::numeric(18,0);
				update ogl_transactions_log set ConvertedDebitamount = ConvertedDebitamountT, ConvertedCreditamount = ConvertedCreditamountT;
				alter table ogl_transactions_log drop column ConvertedCreditAmountT;
				alter table ogl_transactions_log drop column ConvertedDebitAmountT;

			
				alter table ogl_transactions add column ConvertedCreditAmountT numeric(18,0);
				alter table ogl_transactions add column ConvertedDebitAmountT numeric(18,0);
				update ogl_transactions set ConvertedDebitamount = 0 where converteddebitamount='';
                update ogl_transactions set ConvertedCreditAmount = 0 where ConvertedCreditAmount='';
				update ogl_transactions set ConvertedDebitamountT = Cast(ConvertedDebitamount as numeric(18,0)), ConvertedCreditamountT = Cast(ConvertedCreditamount as numeric(18,0));
				update ogl_transactions set ConvertedDebitamount = 0, ConvertedCreditamount = 0;
