				UPDATE SEQ_DATA_ENGINE_CONFIG SET SEQNO = (SELECT COALESCE(MAX(ID), 1) FROM DATA_ENGINE_CONFIG);
				UPDATE SEQ_DATA_ENGINE_TABLE  SET SEQNO = (SELECT COALESCE(MAX(ID), 1) FROM DATA_ENGINE_TABLE);
				UPDATE SEQ_DATA_ENGINE_COLUMN SET SEQNO = (SELECT COALESCE(MAX(ID), 1) FROM DATA_ENGINE_COLUMN);


	UPDATE SECUSERS SET DELETED = 0;
	UPDATE SECUSERS_TEMP SET DELETED = 0;
	UPDATE SECUSERS_RTEMP SET DELETED = 0;


	ALTER TABLE SECUSERS ADD CONSTRAINT DF_DELETED_SECUSERS DEFAULT 0 FOR DELETED;
	ALTER TABLE SECUSERS_TEMP ADD CONSTRAINT DF_DELETED_SECUSERS_TEMP DEFAULT 0 FOR DELETED;
	ALTER TABLE SECUSERS_RTEMP ADD CONSTRAINT DF_DELETED_SECUSERS_RTEMP DEFAULT 0 FOR DELETED;


	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.NoteId),0)+1 into v_maxval from
	(select NoteId from Notes) T;
	EXECUTE IMMEDIATE 'alter
	sequence SEQNotes restart start with ' || v_maxval;
	end;


	do $$
	declare
	v_maxval INTEGER;
	begin
	select COALESCE(max(T.NoteId),0)+1 into v_maxval from
	(select NoteId from Notes) T;
	EXECUTE 'alter
	sequence SEQNotes restart start with ' || v_maxval;
	end;
	$$;

			
			DECLARE @SQLCmd NVARCHAR(MAX);
			DECLARE @MAXNO BIGINT;
			SELECT
			@MAXNO=COALESCE(max(T.NoteId),0)+1 from
			(select NoteId from Notes) T;

			SET @SQLCmd = '
			CREATE
			SEQUENCE [dbo].[SEQNotes]
			AS [bigint]
			START WITH '
			+ CAST( @MAXNO
			AS VARCHAR(30)) +
			'INCREMENT BY 1
			MINVALUE
			-9223372036854775808
			MAXVALUE 9223372036854775807
			CACHE ';


			EXEC
			SP_EXECUTESQL
			@SQLCmd;
