changeSet.createProcedure:
CREATE OR REPLACE procedure SP_FINCON_GL(exe_postdate date) AS BEGIN execute immediate 'truncate table FINCON_GL_STG'; DECLARE PERIOD VARCHAR2(20 CHAR); ACC_DATE VARCHAR2(20 CHAR); BATCH_NAME VARCHAR2(20 CHAR);
Begin SELECT TO_CHAR(TO_DATE(SYSPARMVALUE,'YYYY-MM-DD'),'Mon-yy') into PERIOD FROM SMTPARAMETERS where SYSPARMCODE='APP_DATE'; SELECT TO_CHAR(TO_DATE(SYSPARMVALUE,'YYYY-MM-DD'),'DD-Mon-YY') into ACC_DATE FROM SMTPARAMETERS where SYSPARMCODE='APP_DATE'; SELECT TO_CHAR(TO_DATE(SYSPARMVALUE,'YYYY-MM-DD'),'DD-Mon-yyyy') into BATCH_NAME FROM SMTPARAMETERS where SYSPARMCODE='APP_DATE';
INSERT INTO FINCON_GL_STG (BRANCH,CURRENCY,CURRENCYCODE,DR_AMOUNT,CR_AMOUNT,JOURNAL_SOURCE,JOURNAL_CATEGORY,SOURCE_GL_CODE,BOOKS,CONST1,CONST2,PERIOD,ACC_DATE,BATCH_NAME, JOURNAL_NAME) select po.postbranch AS BRANCH, 'INR' AS CURRENCY, 'INR' AS CURRENCYCODE, CASE WHEN PO.DRORCR='D' THEN SUM(PO.POSTAMOUNT) ELSE 0 END AS DR_AMOUNT, CASE WHEN PO.DRORCR='C' THEN SUM(PO.POSTAMOUNT) ELSE 0 END AS CR_AMOUNT, 'PN' AS JOURNAL_SOURCE, 'File 1: Closing TB' AS JOURNAL_CATEGORY, PERIOD, ACC_DATE, 'PNFile 1: Closing TB'||BATCH_NAME, 'PNFile 1: Closing TB'|| TO_CHAR((TO_DATE((ACC_DATE),'DD-Mon-yyyy')-1),'DD-Mon-yyyy') JOURNAL_NAME, AM.HOSTACCOUNT AS SOURCE_GL_CODE, '1' AS BOOKS, 'NEW' AS CONST1, 'A' AS CONST2 From postings PO INNER JOIN ACCOUNTMAPPING AM ON PO.ACCOUNT = AM.ACCOUNT WHERE PO.POSTDATE = exe_postdate GROUP BY po.postbranch,PO.DRORCR,AM.HOSTACCOUNT; END; COMMIT; end SP_FINCON_GL;

CREATE OR REPLACE PROCEDURE SP_EXTRACT_UCIC_DATA AS BEGIN DBMS_OUTPUT.PUT_LINE (SYSTIMESTAMP); BEGIN EXECUTE IMMEDIATE 'DROP TABLE CUSTOMERS_MODIFICATIONS'; EXCEPTION WHEN OTHERS THEN IF SQLCODE != -942 THEN RAISE; END IF; END; BEGIN EXECUTE IMMEDIATE 'CREATE TABLE CUSTOMERS_MODIFICATIONS AS SELECT CUSTID,FINID,CUSTCIF,FINREFERENCE,TO_DATE(''01-01-23'',''DD-MM-YY'') AS CREATION_DATE,CUSTCOREBANK,SOURCE_SYSTEM,CUSTCTGCODE ,CUSTOMER_TYPE,SUBCATEGORY ,CUSTSHRTNAME,CUSTDOB,''NA'' AS CUSTISBLOCKED,CLOSEDDATE ,CLOSINGSTATUS,CUSTMOTHERMAIDEN,ACCNUMBER ,COMPANYNAME , EMAIL1,EMAIL2,EMAIL3,MOBILE1,MOBILE2,MOBILE3,LANDLINE1 ,LANDLINE2 ,LANDLINE3 ,PAN ,AADHAAR,VOTER_ID ,DRIVING_LICENCE ,PASSPORT ,ADDR1TYPE ,ADDR1LINE1 ,ADDR1LINE2 ,ADDR1LINE3 ,ADDR1LINE4 ,ADDR1CITY ,ADDR1STATE ,ADDR1PIN ,ADDR2TYPE , ADDR2LINE1 ,ADDR2LINE2 ,ADDR2LINE3 ,ADDR2LINE4 ,ADDR2CITY ,ADDR2STATE ,ADDR2PIN ,ADDR3TYPE ,ADDR3LINE1 ,ADDR3LINE2 ,ADDR3LINE3 ,ADDR3LINE4 ,ADDR3CITY ,ADDR3STATE ,ADDR3PIN ,LASTMNTON,INSERT_UPDATE_FLAG, (SELECT SYSPARMVALUE FROM SMTPARAMETERS WHERE SYSPARMCODE = ''APP_DATE'' ) AS PROCESS_DATE FROM ( SELECT /*+ PARALLEL(4 ) */ C.CUSTID,FM.FINID,C.CUSTCIF,FM.FINREFERENCE,C.CUSTCOREBANK,''PLF'' AS SOURCE_SYSTEM,''NA'' AS LANDLINE1,''NA'' AS LANDLINE2, ''NA'' AS LANDLINE3,C.CUSTCTGCODE,''NA'' AS CUSTOMER_TYPE, C.SUBCATEGORY,C.CUSTSHRTNAME,C.CUSTDOB,FM.CLOSEDDATE,FM.CLOSINGSTATUS, C.CUSTMOTHERMAIDEN,C.LASTMNTON,MD.ACCNUMBER,CE.COMPANYNAME, (SELECT CUSTEMAIL FROM CUSTOMEREMAILS EM WHERE EM.CUSTEMAILPRIORITY = 5 AND EM.CUSTID = C.CUSTID AND ROWNUM =1) AS EMAIL1, (SELECT CUSTEMAIL FROM CUSTOMEREMAILS EM WHERE EM.CUSTEMAILPRIORITY = 4 AND EM.CUSTID = C.CUSTID AND ROWNUM =1) AS EMAIL2, (SELECT CUSTEMAIL FROM CUSTOMEREMAILS EM WHERE EM.CUSTEMAILPRIORITY = 3 AND EM.CUSTID = C.CUSTID AND ROWNUM =1) AS EMAIL3, (SELECT PHONENUMBER FROM CUSTOMERPHONENUMBERS WHERE PHONETYPEPRIORITY = 5 AND PHONECUSTID = C.CUSTID) AS MOBILE1, (SELECT PHONENUMBER FROM CUSTOMERPHONENUMBERS WHERE PHONETYPEPRIORITY = 4 AND PHONECUSTID = C.CUSTID) AS MOBILE2, (SELECT PHONENUMBER FROM CUSTOMERPHONENUMBERS WHERE PHONETYPEPRIORITY = 3 AND PHONECUSTID = C.CUSTID) AS MOBILE3, (SELECT CUSTDOCTITLE FROM CUSTOMERDOCUMENTS CD WHERE CUSTDOCCATEGORY IN (SELECT KEY_TYPE FROM MASTER_DEF WHERE KEY_TYPE = ''PAN'') AND CD.CUSTID = C.CUSTID) AS PAN , (SELECT CUSTDOCTITLE FROM CUSTOMERDOCUMENTS CD WHERE CUSTDOCCATEGORY IN (SELECT KEY_TYPE FROM MASTER_DEF WHERE KEY_TYPE = ''AADHAAR'') AND CD.CUSTID = C.CUSTID) AS AADHAAR , (SELECT CUSTDOCTITLE FROM CUSTOMERDOCUMENTS CD WHERE CUSTDOCCATEGORY IN (SELECT KEY_TYPE FROM MASTER_DEF WHERE KEY_TYPE = ''VOTER_ID'') AND CD.CUSTID = C.CUSTID) AS VOTER_ID , (SELECT CUSTDOCTITLE FROM CUSTOMERDOCUMENTS CD WHERE CUSTDOCCATEGORY IN (SELECT KEY_TYPE FROM MASTER_DEF WHERE KEY_TYPE = ''DRIVING_LICENCE'') AND CD.CUSTID = C.CUSTID) AS DRIVING_LICENCE , (SELECT CUSTDOCTITLE FROM CUSTOMERDOCUMENTS CD WHERE CUSTDOCCATEGORY IN (SELECT KEY_TYPE FROM MASTER_DEF WHERE KEY_TYPE = ''PASSPORT'') AND CD.CUSTID = C.CUSTID) AS PASSPORT , A1.CUSTADDRTYPE ADDR1TYPE ,A1.CUSTADDRLINE1 ADDR1LINE1,A1.CUSTADDRLINE2 ADDR1LINE2,A1.CUSTADDRLINE3 ADDR1LINE3,A1.CUSTADDRLINE3 ADDR1LINE4,A1.CUSTADDRCITY ADDR1CITY,A1.CUSTADDRPROVINCE ADDR1STATE, A1.CUSTADDRZIP ADDR1PIN, A2.CUSTADDRTYPE ADDR2TYPE ,A2.CUSTADDRLINE1 ADDR2LINE1,A2.CUSTADDRLINE2 ADDR2LINE2,A2.CUSTADDRLINE3 ADDR2LINE3,A2.CUSTADDRLINE3 ADDR2LINE4,A2.CUSTADDRCITY ADDR2CITY,A2.CUSTADDRPROVINCE ADDR2STATE, A2.CUSTADDRZIP ADDR2PIN, A3.CUSTADDRTYPE ADDR3TYPE ,A3.CUSTADDRLINE1 ADDR3LINE1,A3.CUSTADDRLINE2 ADDR3LINE2,A3.CUSTADDRLINE3 ADDR3LINE3,A3.CUSTADDRLINE3 ADDR3LINE4,A3.CUSTADDRCITY ADDR3CITY,A3.CUSTADDRPROVINCE ADDR3STATE, A3.CUSTADDRZIP ADDR3PIN,'' '' AS INSERT_UPDATE_FLAG FROM CUSTOMERS C LEFT JOIN (SELECT CUSTID,CUSTADDRTYPE,CUSTADDRLINE1,CUSTADDRLINE2,CUSTADDRLINE3,CUSTADDRCITY,CUSTADDRPROVINCE,CUSTADDRZIP FROM CUSTOMERADDRESSES WHERE CUSTADDRPRIORITY = 5 ) A1 ON A1.CUSTID = C.CUSTID LEFT JOIN (SELECT CUSTID,CUSTADDRTYPE,CUSTADDRLINE1,CUSTADDRLINE2,CUSTADDRLINE3,CUSTADDRCITY,CUSTADDRPROVINCE,CUSTADDRZIP FROM CUSTOMERADDRESSES WHERE CUSTADDRPRIORITY = 4 ) A2 ON A2.CUSTID = C.CUSTID LEFT JOIN (SELECT CUSTID,CUSTADDRTYPE,CUSTADDRLINE1,CUSTADDRLINE2,''NA'' AS CUSTADDRLINE3,''NA'' AS CUSTADDRLINE4,CUSTADDRCITY,CUSTADDRPROVINCE,CUSTADDRZIP FROM CUSTOMERADDRESSES WHERE CUSTADDRPRIORITY = 3 ) A3 ON A3.CUSTID = C.CUSTID INNER JOIN FINANCEMAIN FM ON FM.CUSTID = C.CUSTID LEFT JOIN MANDATES MD ON MD.CUSTID = C.CUSTID AND MD.MANDATETYPE = ''SI'' AND FM.MANDATEID = MD.MANDATEID LEFT JOIN FINGUARANTORSDETAILS G ON G.GUARANTORCIF = C.CUSTCIF LEFT JOIN FINJOINTACCOUNTDETAILS J ON J.CUSTCIF = C.CUSTCIF LEFT JOIN CUSTOMEREMPDETAILS CE ON CE.CUSTID = C.CUSTID AND CURRENTEMPLOYER = 1 ) T '; END; COMMIT; DBMS_OUTPUT.PUT_LINE ('CREATED TEMP TABLE CUSTOMERS_MODIFICATIONS');
INSERT INTO UCIC_CUSTOMERS_HISTORY (CUSTID,CUSTCIF,FINREFERENCE,CREATION_DATE,CUSTCOREBANK,SOURCE_SYSTEM,CUSTCTGCODE ,CUSTOMER_TYPE,SUBCATEGORY ,CUSTSHRTNAME,CUSTDOB,CUSTISBLOCKED,CLOSEDDATE ,CLOSINGSTATUS,CUSTMOTHERMAIDEN,ACCNUMBER ,COMPANYNAME ,EMAIL1,EMAIL2,EMAIL3,MOBILE1,MOBILE2,MOBILE3,LANDLINE1 ,LANDLINE2 ,LANDLINE3 ,PAN ,AADHAAR,VOTER_ID ,DRIVING_LICENCE ,PASSPORT ,ADDR1TYPE ,ADDR1LINE1 ,ADDR1LINE2 ,ADDR1LINE3 ,ADDR1LINE4 ,ADDR1CITY ,ADDR1STATE ,ADDR1PIN ,ADDR2TYPE ,ADDR2LINE1 ,ADDR2LINE2 ,ADDR2LINE3 ,ADDR2LINE4 ,ADDR2CITY ,ADDR2STATE ,ADDR2PIN ,ADDR3TYPE ,ADDR3LINE1 ,ADDR3LINE2 ,ADDR3LINE3 ,ADDR3LINE4 ,ADDR3CITY ,ADDR3STATE ,ADDR3PIN ,INSERT_UPDATE_FLAG,LASTMNTON,PROCESS_DATE,FINID ) (SELECT CUSTID,CUSTCIF,FINREFERENCE,CREATION_DATE,CUSTCOREBANK,SOURCE_SYSTEM,CUSTCTGCODE ,CUSTOMER_TYPE,SUBCATEGORY ,CUSTSHRTNAME,CUSTDOB,CUSTISBLOCKED,CLOSEDDATE ,CLOSINGSTATUS,CUSTMOTHERMAIDEN,ACCNUMBER ,COMPANYNAME ,EMAIL1,EMAIL2,EMAIL3,MOBILE1,MOBILE2,MOBILE3,LANDLINE1 ,LANDLINE2 ,LANDLINE3 ,PAN ,AADHAAR,VOTER_ID ,DRIVING_LICENCE ,PASSPORT ,ADDR1TYPE ,ADDR1LINE1 ,ADDR1LINE2 ,ADDR1LINE3 ,ADDR1LINE4 ,ADDR1CITY ,ADDR1STATE ,ADDR1PIN ,ADDR2TYPE ,ADDR2LINE1 ,ADDR2LINE2 ,ADDR2LINE3 ,ADDR2LINE4 ,ADDR2CITY ,ADDR2STATE ,ADDR2PIN ,ADDR3TYPE ,ADDR3LINE1 ,ADDR3LINE2 ,ADDR3LINE3 ,ADDR3LINE4 ,ADDR3CITY ,ADDR3STATE ,ADDR3PIN ,INSERT_UPDATE_FLAG,LASTMNTON,PROCESS_DATE,FINID FROM UCIC_CUSTOMERS T1 WHERE (CUSTID,CUSTCIF) IN (SELECT CUSTID,CUSTCIF FROM (SELECT CUSTID,CUSTCIF,CUSTSHRTNAME FROM UCIC_CUSTOMERS MINUS SELECT CUSTID,CUSTCIF,CUSTSHRTNAME FROM CUSTOMERS ) )) ; COMMIT; DBMS_OUTPUT.PUT_LINE ('INSERTED TO UCIC_CUSTOMERS_HISTORY');
DELETE FROM UCIC_CUSTOMERS WHERE (CUSTID,CUSTCIF) IN(SELECT CUSTID,CUSTCIF FROM UCIC_CUSTOMERS WHERE (CUSTID,CUSTCIF) IN (SELECT CUSTID,CUSTCIF FROM CUSTOMERS_MODIFICATIONS)); COMMIT; DBMS_OUTPUT.PUT_LINE ('DELETED IN UCIC_CUSTOMERS'); INSERT INTO UCIC_CUSTOMERS (CUSTID,CUSTCIF,FINREFERENCE,CUSTCOREBANK,SOURCE_SYSTEM,CUSTCTGCODE ,CREATION_DATE,CUSTOMER_TYPE,SUBCATEGORY ,CUSTSHRTNAME,CUSTDOB,CUSTISBLOCKED,CLOSEDDATE ,CLOSINGSTATUS,CUSTMOTHERMAIDEN,ACCNUMBER ,COMPANYNAME ,EMAIL1,EMAIL2,EMAIL3,MOBILE1,MOBILE2,MOBILE3,LANDLINE1 ,LANDLINE2 ,LANDLINE3 ,PAN ,AADHAAR,VOTER_ID ,DRIVING_LICENCE ,PASSPORT ,ADDR1TYPE ,ADDR1LINE1 ,ADDR1LINE2 ,ADDR1LINE3 ,ADDR1LINE4 ,ADDR1CITY ,ADDR1STATE ,ADDR1PIN ,ADDR2TYPE ,ADDR2LINE1 ,ADDR2LINE2 ,ADDR2LINE3 ,ADDR2LINE4 ,ADDR2CITY ,ADDR2STATE ,ADDR2PIN ,ADDR3TYPE ,ADDR3LINE1 ,ADDR3LINE2 ,ADDR3LINE3 ,ADDR3LINE4 ,ADDR3CITY ,ADDR3STATE ,ADDR3PIN ,INSERT_UPDATE_FLAG,LASTMNTON,PROCESS_DATE,FINID) (SELECT CUSTID,CUSTCIF,FINREFERENCE,CUSTCOREBANK,SOURCE_SYSTEM,CUSTCTGCODE ,TO_DATE(CREATION_DATE,'YYYY-MM-DD hh24:mi:ss'),CUSTOMER_TYPE,SUBCATEGORY ,CUSTSHRTNAME,CUSTDOB,CUSTISBLOCKED,CLOSEDDATE ,CLOSINGSTATUS,CUSTMOTHERMAIDEN,ACCNUMBER ,COMPANYNAME ,EMAIL1,EMAIL2,EMAIL3,MOBILE1,MOBILE2,MOBILE3,LANDLINE1 ,LANDLINE2 ,LANDLINE3 ,PAN ,AADHAAR,VOTER_ID ,DRIVING_LICENCE ,PASSPORT ,ADDR1TYPE ,ADDR1LINE1 ,ADDR1LINE2 ,ADDR1LINE3 ,ADDR1LINE4 ,ADDR1CITY ,ADDR1STATE ,ADDR1PIN ,ADDR2TYPE ,ADDR2LINE1 ,ADDR2LINE2 ,ADDR2LINE3 ,ADDR2LINE4 ,ADDR2CITY ,ADDR2STATE ,ADDR2PIN ,ADDR3TYPE ,ADDR3LINE1 ,ADDR3LINE2 ,ADDR3LINE3 ,ADDR3LINE4 ,ADDR3CITY ,ADDR3STATE ,ADDR3PIN ,INSERT_UPDATE_FLAG,LASTMNTON,TO_DATE(PROCESS_DATE,'YYYY-MM-DD hh24:mi:ss'),FINID FROM CUSTOMERS_MODIFICATIONS WHERE (CUSTID,CUSTCIF) NOT IN (SELECT CUSTID,CUSTCIF FROM UCIC_CUSTOMERS)); COMMIT; DBMS_OUTPUT.PUT_LINE ('INSERTED UCIC_CUSTOMERS');
END SP_EXTRACT_UCIC_DATA;

			create or replace Procedure SP_UCIC_WRITE_REQUEST_FILE(aFileName in varchar2)
			as
			fDirectory varchar(30)
			:= 'DIR_PLF';
			pFile Utl_File.file_type;
			fLine Varchar2(1024);
			CUSTCOREBANK NUMBER(19);
			SOURCE_SYSTEM VARCHAR2(100);
			CUSTID NUMBER(19);
			CUSTCTGCODE VARCHAR2(100);
			CUSTOMER_TYPE VARCHAR2(100);
			SUBCATEGORY VARCHAR2(100);
			CUSTSHRTNAME
			VARCHAR2(100);
			CUSTDOB DATE;
			ADDR1TYPE VARCHAR2(100);
			ADDR1LINE2 VARCHAR2(100);
			ADDR1LINE3 VARCHAR2(100);
			ADDR1LINE4
			VARCHAR2(100);
			ADDR1CITY VARCHAR2(100);
			ADDR1STATE VARCHAR2(100);
			ADDR1PIN VARCHAR2(100);
			EMAIL1 VARCHAR2(100);
			EMAIL2
			VARCHAR2(100);
			EMAIL3 VARCHAR2(100);
			MOBILE1 VARCHAR2(100);
			MOBILE2 VARCHAR2(100);
			MOBILE3 VARCHAR2(100);
			LANDLINE1
			VARCHAR2(100);
			LANDLINE2 VARCHAR2(100);
			LANDLINE3 VARCHAR2(100);
			PAN VARCHAR2(100);
			AADHAAR VARCHAR2(100);
			CUSTMOTHERMAIDEN VARCHAR2(100);
			CREATION_DATE DATE;
			LASTMNTON VARCHAR2(100);
			CUSTISBLOCKED VARCHAR2(100);
			CLOSEDDATE
			DATE;
			INSERT_UPDATE_FLAG VARCHAR2(100);
			ADDR2TYPE VARCHAR2(100);
			ADDR2LINE1 VARCHAR2(100);
			ADDR2LINE2 VARCHAR2(100);
			ADDR2LINE3 VARCHAR2(100);
			ADDR2LINE4 VARCHAR2(100);
			ADDR2CITY VARCHAR2(100);
			ADDR2STATE VARCHAR2(100);
			ADDR2PIN
			VARCHAR2(100);
			ADDR3TYPE VARCHAR2(100);
			ADDR3LINE1 VARCHAR2(100);
			ADDR3LINE2 VARCHAR2(100);
			ADDR3LINE3 VARCHAR2(100);
			ADDR3LINE4 VARCHAR2(100);
			ADDR3CITY VARCHAR2(100);
			ADDR3STATE VARCHAR2(100);
			ADDR3PIN VARCHAR2(100);
			COMPANYNAME
			VARCHAR2(100);
			VOTER_ID VARCHAR2(100);
			PASSPORT VARCHAR2(100);
			DRIVING_LICENCE VARCHAR2(100);
			ACCNUMBER VARCHAR2(100);
			FINREFERENCE VARCHAR2(100);
			NEWREC NUMBER(19);
			MODREC NUMBER(19);
			ALLREC NUMBER(19);

			Begin
			pFile :=
			UTL_FILE.fopen(fDirectory, aFileName, 'w');


			SELECT COUNT(*) INTO newRec FROM UCIC_CUSTOMERS WHERE
			INSERT_UPDATE_FLAG = 'N' AND PROCESS_DATE = (SELECT TO_CHAR(
			TO_DATE(SYSPARMVALUE
			,'YYYY-MM-DD')
			,'DD-MM-YY')
			FROM
			SMTPARAMETERS where SYSPARMCODE='APP_DATE');
			SELECT COUNT(*) INTO
			modRec FROM UCIC_CUSTOMERS WHERE
			INSERT_UPDATE_FLAG='M' AND PROCESS_DATE = (SELECT TO_CHAR(
			TO_DATE(SYSPARMVALUE
			,'YYYY-MM-DD')
			,'DD-MM-YY')
			FROM
			SMTPARAMETERS where SYSPARMCODE='APP_DATE');
			SELECT COUNT(*) INTO allRec FROM
			UCIC_CUSTOMERS WHERE PROCESS_DATE =
			(SELECT TO_CHAR(
			TO_DATE(SYSPARMVALUE
			,'YYYY-MM-DD')
			,'DD-MM-YY')
			FROM SMTPARAMETERS
			where SYSPARMCODE='APP_DATE');

			fLine
			:= 'HDR|' || allRec || '|A|' ||newRec || '|M|' || modRec ;
			UTL_FILE.put_line(pFile, convert(fLine, 'WE8ISO8859P1',
			'UTF8'));

			UTL_FILE.put_line(pFile,'UCIC|Source
			system|UniqueID|Customer Category|Customer Type|Customer Type
			Desc|Customer Full
			Name|DOB|AddressType1|Address1|Address1 City|Address1 State|Address1 Pincode|EMail 1|EMail 2|EMail
			3|Mobile
			No1|Mobile
			No2|Mobile No3|Land Phone1|Land Phone2|Land Phone3|PAN No|Aadhar Number|Mothers Maiden
			Name|Creation
			Date|Updation
			date|Blocked Flag|Blocked Code and Desc|Blocked eff. date|Insert Update Flag|Address2
			Type|Address2|Address2
			City|Address2 State|Address2 Pincode|Address3 Type|Address3|Address3 City|Address3
			State|Address3 Pincode|Employer
			Name|Voter ID|Passport No|Drining Lic No|Casa AccountNo|Loan Number');

			For UCIC_CUSTOMERS IN (Select * From UCIC_CUSTOMERS WHERE PROCESS_DATE = (SELECT TO_CHAR(
			TO_DATE(SYSPARMVALUE
			,'YYYY-MM-DD')
			,'DD-MM-YY')
			FROM SMTPARAMETERS where SYSPARMCODE='APP_DATE')) Loop
			UTL_FILE.PUT_LINE(pFile,UCIC_CUSTOMERS.CUSTCOREBANK ||'|"'|| UCIC_CUSTOMERS.SOURCE_SYSTEM ||'"|"'||
			UCIC_CUSTOMERS.CUSTID ||'|"'|| UCIC_CUSTOMERS.CUSTCTGCODE ||'"|"'||
			UCIC_CUSTOMERS.CUSTOMER_TYPE ||'"|"'||
			UCIC_CUSTOMERS.SUBCATEGORY||'"|"'|| UCIC_CUSTOMERS.CUSTSHRTNAME||'"|'||
			UCIC_CUSTOMERS.CUSTDOB||'|"'||
			UCIC_CUSTOMERS.ADDR1TYPE||'"|"'||
			UCIC_CUSTOMERS.ADDR1LINE1||UCIC_CUSTOMERS.ADDR1LINE2|| UCIC_CUSTOMERS.ADDR1LINE3||
			UCIC_CUSTOMERS.ADDR1LINE4||'"|"'||
			UCIC_CUSTOMERS.ADDR1CITY||'"|"'|| UCIC_CUSTOMERS.ADDR1STATE||'"|'||
			UCIC_CUSTOMERS.ADDR1PIN||'|"'||
			UCIC_CUSTOMERS.EMAIL1||'"|"'|| UCIC_CUSTOMERS.EMAIL2||'"|"'||
			UCIC_CUSTOMERS.EMAIL3||'"|'||
			UCIC_CUSTOMERS.MOBILE1||'|'|| UCIC_CUSTOMERS.MOBILE2||'|'||
			UCIC_CUSTOMERS.MOBILE3||'|'||
			UCIC_CUSTOMERS.LANDLINE1||'|'|| UCIC_CUSTOMERS.LANDLINE2||'|'||
			UCIC_CUSTOMERS.LANDLINE3||'|"'||
			UCIC_CUSTOMERS.PAN||'"|'|| UCIC_CUSTOMERS.AADHAAR||'|"'||
			UCIC_CUSTOMERS.CUSTMOTHERMAIDEN||'"|'||
			UCIC_CUSTOMERS.CREATION_DATE||'|'|| UCIC_CUSTOMERS.LASTMNTON||'|"'||
			UCIC_CUSTOMERS.CUSTISBLOCKED||'"|"'||
			UCIC_CUSTOMERS.CUSTISBLOCKED||'"|"'|| UCIC_CUSTOMERS.CLOSEDDATE||'"|"'||
			UCIC_CUSTOMERS.INSERT_UPDATE_FLAG||'"|"'||
			UCIC_CUSTOMERS.ADDR2TYPE||'"|"'|| UCIC_CUSTOMERS.ADDR2LINE1||
			UCIC_CUSTOMERS.ADDR2LINE2||UCIC_CUSTOMERS.ADDR2LINE3||
			UCIC_CUSTOMERS.ADDR2LINE4||'|"'||
			UCIC_CUSTOMERS.ADDR2CITY||'"|"'|| UCIC_CUSTOMERS.ADDR2STATE||'"|'||
			UCIC_CUSTOMERS.ADDR2PIN||'|"'||
			UCIC_CUSTOMERS.ADDR3TYPE||'"|"'|| UCIC_CUSTOMERS.ADDR3LINE1||
			UCIC_CUSTOMERS.ADDR3LINE2|| UCIC_CUSTOMERS.ADDR3LINE3||
			UCIC_CUSTOMERS.ADDR3LINE4 ||'"|"'||
			UCIC_CUSTOMERS.ADDR3CITY||'"|"'|| UCIC_CUSTOMERS.ADDR3STATE||'"|'||
			UCIC_CUSTOMERS.ADDR3PIN||'|"'||
			UCIC_CUSTOMERS.COMPANYNAME||'"|"'|| UCIC_CUSTOMERS.VOTER_ID||'"|"'||
			UCIC_CUSTOMERS.PASSPORT||'"|"'||
			UCIC_CUSTOMERS.DRIVING_LICENCE||'"|'|| UCIC_CUSTOMERS.ACCNUMBER||'|"'||
			UCIC_CUSTOMERS.FINREFERENCE||'"|');

			End Loop;


			fLine := 'EOF';
			UTL_FILE.put_line(pFile, convert(fLine,
			'WE8ISO8859P1', 'UTF8'));
			UTL_FILE.fclose(pFile);
			End;

			CREATE OR REPLACE Procedure SP_UCIC_WRITE_WEEKLY_FILE(aFileName in varchar2)
			Is
			fDirectory varchar(30)
			:= 'DIR_PLF';
			pFile Utl_File.file_type;
			fLine Varchar2(1024);
			SOURCE_SYSTEM VARCHAR2(100);
			CUSTID NUMBER(19);
			CUSTISBLOCKED VARCHAR2(100);
			CUSTCOREBANK VARCHAR2(19);
			PAN VARCHAR2(100);
			AADHAAR VARCHAR2(100);
			CUSTCTGCODE
			VARCHAR2(100);
			CUSTOMER_TYPE VARCHAR2(100);
			CUSTDOB DATE;
			CUSTSHRTNAME VARCHAR2(100);
			MOBILE1 VARCHAR2(100);
			EMAIL1
			VARCHAR2(100);
			CREATION_DATE DATE;
			LASTMNTON VARCHAR2(100);

			allRec NUMBER(19);

			Begin
			pFile := UTL_FILE.fopen(fDirectory,
			aFileName, 'w');


			SELECT COUNT(*) INTO allRec FROM UCIC_CUSTOMERS;

			fLine := 'HDR|' || allRec ;
			UTL_FILE.put_line(pFile, convert(fLine,
			'WE8ISO8859P1', 'UTF8'));

			UTL_FILE.put_line(pFile,'UCIC|Source
			system|UniqueID|Customer Category|Customer Type|Customer
			Type Desc|Customer Full
			Name|DOB|AddressType1|Address1|Address1 City|Address1 State|Address1 Pincode|EMail 1|EMail
			2|EMail 3|Mobile
			No1|Mobile No2|Mobile No3|Land Phone1|Land Phone2|Land Phone3|PAN No|Aadhar Number|Mothers Maiden
			Name|Creation
			Date|Updation date|Blocked Flag|Blocked Code and Desc|Blocked eff. date|Insert Update Flag|Address2
			Type|Address2|Address2 City|Address2 State|Address2 Pincode|Address3 Type|Address3|Address3 City|Address3
			State|Address3 Pincode|Employer Name|Voter ID|Passport No|Drining Lic No|Casa AccountNo|Loan Number');

			For UCIC_CUSTOMERS IN (Select * From UCIC_CUSTOMERS) Loop
			UTL_FILE.PUT_LINE(pFile, '~"'
			||UCIC_CUSTOMERS.SOURCE_SYSTEM||
			'"~"' ||UCIC_CUSTOMERS.CUSTID || '"~"' ||
			UCIC_CUSTOMERS.CUSTISBLOCKED|| '"~"'
			||UCIC_CUSTOMERS.CUSTCOREBANK|| '"~"'
			||UCIC_CUSTOMERS.PAN|| '"~"' ||
			UCIC_CUSTOMERS.AADHAAR|| '"~"' ||
			UCIC_CUSTOMERS.CUSTCTGCODE || '"~"' ||
			UCIC_CUSTOMERS.CUSTOMER_TYPE|| '"~"' ||
			UCIC_CUSTOMERS.CUSTDOB|| '"~"' ||
			UCIC_CUSTOMERS.CUSTSHRTNAME || '"~"' ||
			UCIC_CUSTOMERS.MOBILE1 || '"~"' ||
			UCIC_CUSTOMERS.EMAIL1 || '"~"' ||
			UCIC_CUSTOMERS.CREATION_DATE || '"~"' ||
			UCIC_CUSTOMERS.LASTMNTON ||'"~');
			End Loop;


			fLine := 'EOF';
			UTL_FILE.put_line(pFile, convert(fLine, 'WE8ISO8859P1', 'UTF8'));
			UTL_FILE.fclose(pFile);
			End;

			create or replace PROCEDURE SP_READ_UCIC_RESP_FILE(FILENAME IN VARCHAR2) AS
			BEGIN
			DECLARE
			p_dir
			varchar2(2000):='DIR_PLF';
			p_file_name varchar2(2000):=FILENAME;
			l_file UTL_FILE.file_type;
			fileRecord varchar2(32767);
			cusId varchar2(50);
			ucicId varchar2(50);
			begin
			execute immediate 'TRUNCATE TABLE UCIC_CUSTOMERS_STATUS';
			l_file :=
			UTL_FILE.fopen(p_dir,p_file_name,'r');
			dbms_output.put_line( 'READING FILE' );
			UTL_FILE.get_line(l_file,fileRecord);
			UTL_FILE.get_line(l_file,fileRecord);

			LOOP
			UTL_FILE.get_line(l_file,fileRecord);
			IF
			fileRecord != 'TRAILER 20230302000000'
			THEN
			IF fileRecord != 'EOF'
			THEN
			cusId :=
			regexp_substr(fileRecord,'[^,]+', 1, 1);
			cusId := regexp_replace(cusId, '(^[[:space:]]+)|([[:space:]]+$)',null);
			ucicId :=
			TRIM(regexp_substr(fileRecord,'[^,]+', 1, 2));
			ucicId := regexp_replace(ucicId,
			'(^[[:space:]]+)|([[:space:]]+$)',null);
			DBMS_OUTPUT.put_line('Update custId='||cusId || 'where ucicId=' || ucicId);
			INSERT INTO UCIC_CUSTOMERS_STATUS (CUSTID,UCICID,status) VALUES(cusId,ucicId,'SUCCESS');
			UPDATE CUSTOMERS SET
			CUSTCOREBANK = ucicId WHERE CUSTID = cusId ;
			COMMIT;
			END IF;
			END IF;
			END LOOP;
			UTL_FILE.fclose(l_file);
			EXCEPTION
			WHEN
			UTL_FILE.invalid_operation THEN dbms_output.PUT_LINE('cannot open file invalid name');
			WHEN UTL_FILE.read_error THEN
			dbms_output.PUT_LINE('cannot be read');
			WHEN no_data_found THEN dbms_output.PUT_LINE('end of file');
			UTL_FILE.fclose(l_file);
			END;
			END SP_READ_UCIC_RESP_FILE;

			create or replace Procedure SP_UCIC_WRITE_ACK_FILE(aFileName in varchar2)
			Is
			fDirectory varchar(30) :=
			'DIR_PLF';
			pFile Utl_File.file_type;
			fLine Varchar2(1024);
			CUSTID VARCHAR2(40);
			UCICID VARCHAR2(40);
			STATUS VARCHAR2(40);
			ERROR_DESC VARCHAR2(40);

			allRec NUMBER(19);


			Begin
			pFile := UTL_FILE.fopen(fDirectory, aFileName, 'w');

			SELECT COUNT(*) INTO allRec FROM UCIC_CUSTOMERS_STATUS;
			DBMS_OUTPUT.put_line('allRec:'||allRec);
			fLine := 'HDR|' ||
			allRec ||
			'|1' ;
			UTL_FILE.put_line(pFile, convert(fLine, 'WE8ISO8859P1', 'UTF8'));

			UTL_FILE.PUT_LINE(pFile,'CUSTOMER ID|UCIC|STATUS|REASON REJECTION');
			For UCIC_CUSTOMERS_STATUS IN (Select
			CUSTID,UCICID,STATUS,ERROR_DESC From
			UCIC_CUSTOMERS_STATUS)
			Loop
			UTL_FILE.PUT_LINE(pFile,UCIC_CUSTOMERS_STATUS.CUSTID
			||'|'|| UCIC_CUSTOMERS_STATUS.UCICID
			||'|'|| UCIC_CUSTOMERS_STATUS.STATUS||'|'||UCIC_CUSTOMERS_STATUS.ERROR_DESC );
			End Loop;

			fLine := 'EOF';
			UTL_FILE.put_line(pFile, convert(fLine, 'WE8ISO8859P1', 'UTF8'));
			UTL_FILE.fclose(pFile);
			End;
