			UPDATE SEQSECRIGHTS SET SEQNO= (SELECT MAX(RIGHTID) FROM SECRIGHTS);
			UPDATE SEQSECGROUPS SET SEQNO= (SELECT MAX(GRPID) FROM SECGROUPS);


	delete from reasons where reasontypeid IN (select id from reasontypes where code IN ('FIWRES', 'FISTATUS')) and reasoncategoryId IN (select id from reasoncategory where code IN ('FIWRES', 'FISTATUS'));
	delete from reasontypes where code IN ('FIWRES', 'FISTATUS');
	delete from reasoncategory where code IN ('FIWRES', 'FISTATUS');
	
	update seqreasontypes set seqno = (select coalesce(max(Id), 0) from reasontypes_view);
	update seqreasoncategory set seqno = (select coalesce(max(Id), 0) from reasoncategory_view);
	update seqreasons set seqno = (select coalesce(max(Id), 0) from reasons_view);
	
