			UPDATE SEQSECRIGHTS SET SEQNO= (SELECT MAX(RIGHTID) FROM SECRIGHTS);
			UPDATE SEQSECGROUPS SET SEQNO= (SELECT MAX(GRPID) FROM SECGROUPS);



Delete from SecRoleGroups where roleid in(Select roleid from secroles where rolecd in('MSTGRP1_MAKER','MSTGRP1_APPROVER')) and grpid in(Select grpid from secgroups where grpcode IN('TECHNICALVERIFICATION_MAKER','TECHNICALVERIFICATION_APPROVER'));
Delete from Secgrouprights where grpid in(Select grpid from secgroups where grpcode IN('TECHNICALVERIFICATION_MAKER','TECHNICALVERIFICATION_APPROVER')) and rightid in(Select rightid from SecRights where Page IN('TechnicalVerificationDialog','TechnicalVerificationList'));
Delete from Secgrouprights where grpid in(Select grpid from secgroups where grpcode IN('TECHNICALVERIFICATION_MAKER','TECHNICALVERIFICATION_APPROVER')) and rightid in(Select rightid from SecRights where RightName = 'menuItem_Verification_TechnicalVerification');
Delete from SecGroups where GrpCode IN('TECHNICALVERIFICATION_MAKER','TECHNICALVERIFICATION_APPROVER');
Delete from SecRights where page IN('TechnicalVerificationDialog','TechnicalVerificationList');
Delete from SecRights where RightName = 'menuItem_Verification_TechnicalVerification';
			
UPDATE seqSecRoleGroups SET SEQNO= (SELECT MAX(rolegrpid) FROM SecRoleGroups);
UPDATE seqSecgrouprights SET SEQNO= (SELECT MAX(grprightid) FROM Secgrouprights);
UPDATE SEQSECRIGHTS SET SEQNO= (SELECT MAX(RIGHTID) FROM SECRIGHTS);
UPDATE SEQSECGROUPS SET SEQNO= (SELECT MAX(GRPID) FROM SECGROUPS);

INSERT INTO SecRights VALUES ((Select MAX(RightID)+1 From SecRights), 0, 'menuItem_Verification_TechnicalVerification', 'MENU',  1, 1000, current_timestamp, 'Approved',' ',' ',' ',' ',' ',0);
