alter table RejectFinanceMain modify PlanEMIHMethod default null;
			update RejectFinanceMain set PlanEMIHMethod = 0 where PlanEMIHMethod is null;
			create table sai_temp as select * from RejectFinanceMain;
			alter table RejectFinanceMain modify (PlanEMIHMethod NULL);
			update RejectFinanceMain set  PlanEMIHMethod = null;
			alter table RejectFinanceMain modify PlanEMIHMethod char(1);
			update RejectFinanceMain m set (m.PlanEMIHMethod) = (select t.PlanEMIHMethod from sai_temp t where t.FinReference = m.FinReference) where exists (select 1 from sai_temp t where t.FinReference = m.FinReference);
			drop table sai_temp;
			
