UPDATE SEQSECGROUPS SET SEQNO= (SELECT MAX(GRPID) FROM SECGROUPS);
UPDATE SEQSECGROUPRIGHTS SET SEQNO = (SELECT MAX(GRPRIGHTID) FROM SECGROUPRIGHTS); 
UPDATE SEQSECROLEGROUPS SET SEQNO= (SELECT MAX(ROLEGRPID) FROM SECROLEGROUPS);

	
	delete from SecRoleGroups where grpid=(select grpid from secgroups where GrpCode='FinAdvancePayments_Maker')
and roleid=(select RoleID from SecRoles where RoleCd='OPERATIONS_OFFICER');
	update FinAdvancePayments set DisbDate=(select finstartdate from FinanceMain where FinReference=FinAdvancePayments.FinReference), DisbSeq=1 where DisbDate is null;
	update FinAdvancePayments_Temp set DisbDate=(select finstartdate from FinanceMain where FinReference=FinAdvancePayments_Temp.FinReference), DisbSeq=1 where DisbDate is null;
	update FinAdvancePayments_Temp set DisbDate=(select finstartdate from FinanceMain_Temp where FinReference=FinAdvancePayments_Temp.FinReference), DisbSeq=1 where DisbDate is null;
	INSERT INTO SecRoleGroups SELECT (select MAX(RoleGrpID)+1 from SecRoleGroups),(select grpid from secgroups where GrpCode='FinAdvancePayments_Maker'), (select RoleID from SecRoles where RoleCd='OPERATIONS_OFFICER'),0,1000,null,' ',' ',' ',' ',' ',' ',0 FROM SeqSecRoleGroups WHERE 0=0;

update PTApplicationDetails set AppDescription='pennApps Lending Factory';
INSERT INTO SecRights SELECT (select (SeqNo+1) from SeqSecRights),3,'StepPolicyDialog_stepType','StepPolicyDialog',0,1000,null,'Approved',' ',' ',' ',' ',' ',0 FROM SeqSecRights WHERE 0=0;
