	(Select GrpID from SecGroups where GrpCode = 'CL_ORIGINATION_WRM'),
	 (Select RightID from SecRights where RightName = 'button_FinanceMainList_NewFinanceMain'),
	1, 0, 1000, CURRENT_TIMESTAMP, null, null, null, null, null, null, 0 );

			Delete from SecGroupRights where RightID IN (
            select RightID from SecRights where RightName = 'button_FinanceMainList_NewFinanceMain');             
